                      bVal = this.tooltipData.seriesObj[b] ? this.tooltipData.seriesObj[b].value[this.completeSeriesConfig[b].y] : Number.MIN_VALUE;


                  return bVal - aVal;
                }
      },
      "combined": {
        "name": "Combined",
        "selected": true,
        "sortOnSeriesChange": ["nameIgnoreCase", "muted"],
